function getRandomColor(){var c="0123456789ABCDEF".split("");var a="#";for(var b=0;b<6;b++){a+=c[Math.round(Math.random()*15)]}return a}var clickedTime;var createdTime;var reactionTime;function makeBox(){createdTime=Date.now();var a=Math.random();a=a*5000;setTimeout(function(){if(Math.random()>0.5){document.getElementById("box").style.borderRadius="100px"}else{document.getElementById("box").style.borderRadius="0px"}var c=Math.random();var b=Math.random();c=c*300;b=b*300;document.getElementById("box").style.top=c+"px";document.getElementById("box").style.left=b+"px";document.getElementById("box").style.backgroundColor=getRandomColor();document.getElementById("box").style.display="block"},a)}document.getElementById("box").onclick=function(){clickedTime=Date.now();reactionTime=(clickedTime-createdTime)/1000;document.getElementById("time").innerHTML=reactionTime;this.style.display="none";makeBox()};makeBox();